	, MEANING
	, ACCOUNTNUMBER
)
values (
	 customseq.nextVal
	, #{kanji}
	, #{yomigana}
	, #{meaning}
	, #{accountNumber}
)

insert into blinkGameTable (
	blinkgamenumber
	, accountNumber
	, clearWordCount
	, maxCombo
	, highScore
)
values (
	blinkGameNumberSeq.nextVal
	, #{accountNumber}
	, #{clearWordCount}
	, #{maxCombo}
	, #{highScore}
)

mapper.update:
update blinkGameTable
set clearWordCount = #{clearWordCount}
	, maxCombo = #{maxCombo}
	, highScore = #{highScore}
where accountNumber = #{accountNumber}

update customMemorize
set Kanji = #{kanji}
	, Yomigana = #{yomigana}
	, Meaning = #{meaning}
where WordNumber = #{wordnumber} and 
		accountNumber = #{accountNumber}

mapper.select:
select * from UserAccount
